mapper.select:
        SELECT *
        FROM 

			serviceName = #{serviceName}
			AND serviceNo = #{serviceNo}
			AND fileNo = #{fileNo}

       	ORDER BY
       		fileNo ASC

        SELECT *
        FROM 

			serviceName = #{serviceName}
			AND serviceNo = #{serviceNo}

       	ORDER BY
       		fileNo ASC

mapper.delete:
        DELETE FROM 

			serviceName = #{serviceName}
			AND serviceNo = #{serviceNo}
			AND fileNo = #{fileNo}
